import React, { Component } from 'react';
import { Link, withRouter } from "react-router-dom";
import { AuthContent, InputWithLabel, AuthButton, RightAlignedLink } from 'components/Auth';
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "actions/authActions";
import classnames from "classnames";



class RegisterBook extends Component {

    constructor() {
        super();
        this.state = {
          bookname: "",
          author: "",
          subject: "",
          subject_category: "",
          status: "대여 가능",
          register_user: ""
        };
      }

     

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
      };
    onSubmit = e => {
        e.preventDefault();
    const newBook = {
        bookname: this.state.bookname,
        author: this.state.author,
        subject: this.state.subject,
        subject_category: this.state.subject_category,
        status: "대여 가능",
        register_user: ""
        };
    console.log(newBook);
    this.props.registerUser(newBook, this.props.history); 
      };


      render() {
        console.log("register");
            const { errors } = this.state;
        return (
            <AuthContent title="책 등록하기">
                <form onSubmit={this.onSubmit}>

                <InputWithLabel 
                    label="책 이름" 
                    name="name" 
                    placeholder="책 이름" 
                    onChange={this.onChange}
                    value={this.state.bookname}
                    error={errors.bookname}
                    id="name"
                    type="name"      
                />
                <InputWithLabel 
                    label="저자"
                    name="author"
                    placeholder="저자" 
                    onChange={this.onChange}
                    value={this.state.email}
                    error={errors.email}
                    id="author"
                    type="text"
             
                />
                <InputWithLabel 
                    label="과목 이름" 
                    name="password" 
                    placeholder="과목 이름"
                    
                    onChange={this.onChange}
                    value={this.state.password}
                    error={errors.password}
                    id="password"
                    type="password"
                    className={classnames("", {
                        invalid: errors.password
                      })}
         
                />
                <InputWithLabel 
                    label="과목 분류" 
                    name="passwordConfirm" 
                    placeholder="과목 분류" 
                    type="password" 
                    onChange={this.onChange}
                    value={this.state.password2}
                    error={errors.password2}
                    id="password2"
                    className={classnames("", {
                        invalid: errors.password2
                      })}
  
                />
                <AuthButton onClick={this.onSubmit}>등록하기</AuthButton>
                </form>
            </AuthContent>
        );
    }
}

Register.propTypes = {
    registerUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
  };
  const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
  });

export default connect(
    mapStateToProps,
    { registerUser }
  )(withRouter(Register));
